// === LAYOUT/INCLUDES ===
async function loadHeaderFooter(){try{const c=await fetch('includes/tailwind-config.html');document.head.insertAdjacentHTML('beforeend',await c.text());const h=await fetch('includes/header.html');document.getElementById('header-placeholder').innerHTML=await h.text();const f=await fetch('includes/footer.html');document.getElementById('footer-placeholder').innerHTML=await f.text();const b=await fetch('includes/floating-call-button.html');document.body.insertAdjacentHTML('beforeend',await b.text());highlightActiveMenu();initMenuToggle();initSearchToggle();initUserAccountNotification();initCartNotification()}catch(e){console.error('Error loading header/footer:',e)}}
function highlightActiveMenu(){const p=window.location.pathname.split('/').pop()||'index.html';document.querySelectorAll('.nav-link').forEach(l=>{const href=l.getAttribute('href');if(href===p||(p===''&&href==='index.html')){l.classList.add('active','text-blue-600','font-semibold')}})}
function initMenuToggle(){const t=document.querySelector('.menu-toggle'),m=document.getElementById('mobileMenu');if(t&&m){t.addEventListener('click',()=>{m.classList.toggle('hidden');const e=!m.classList.contains('hidden');t.setAttribute('aria-expanded',e);const s=t.querySelectorAll('span');if(e){s[0].style.transform='rotate(45deg) translateY(8px)';s[1].style.opacity='0';s[2].style.transform='rotate(-45deg) translateY(-8px)'}else{s[0].style.transform='';s[1].style.opacity='';s[2].style.transform=''}});window.addEventListener('resize',()=>{if(window.innerWidth>=1024){m.classList.add('hidden');t.setAttribute('aria-expanded','false');t.querySelectorAll('span').forEach(sp=>{sp.style.transform='';sp.style.opacity=''})}})}}
function initSearchToggle(){const a=document.getElementById('searchToggle'),b=document.getElementById('searchBar');if(a&&b){a.addEventListener('click',()=>{b.classList.toggle('hidden');if(!b.classList.contains('hidden')){const i=b.querySelector('input');if(i){setTimeout(()=>i.focus(),100)}}});document.addEventListener('keydown',e=>{if(e.key==='Escape'&&!b.classList.contains('hidden')){b.classList.add('hidden')}})}}
function initUserAccountNotification(){const u=document.getElementById('userAccountBtn');if(u){u.addEventListener('click',e=>{e.preventDefault();showNotification('Tính năng đăng nhập đang được phát triển','info')})}}
function initCartNotification(){const c=document.getElementById('cartBtn');if(c){c.addEventListener('click',e=>{e.preventDefault();showNotification('Tính năng giỏ hàng đang được phát triển','info')})}}
function showNotification(message,type='info'){const n=document.createElement('div');const bg=type==='info'?'bg-blue-600':'bg-green-600';n.className=`fixed top-20 right-4 ${bg} text-white px-6 py-4 rounded-lg shadow-2xl z-50 animate-slide-down`;n.innerHTML=`<div class="flex items-center space-x-3"><i class="fas fa-info-circle text-2xl"></i><div><p class="font-semibold mb-1">Thông báo</p><p class="text-sm">${message}</p></div></div>`;document.body.appendChild(n);setTimeout(()=>{n.style.opacity='0';n.style.transform='translateY(-20px)';n.style.transition='all 0.3s ease-out';setTimeout(()=>{n.remove()},300)},3000)}
loadHeaderFooter();

// === MAIN ===
window.addEventListener('scroll',()=>{const y=window.pageYOffset;const h=document.querySelector('.header');if(h){if(y>100){h.classList.add('sticky')}else{h.classList.remove('sticky')}}});
function formatCurrency(a){return new Intl.NumberFormat('vi-VN',{style:'currency',currency:'VND'}).format(a)}
function renderFeaturedProducts(){const g=document.querySelector('#featuredProductsGrid');if(!g||typeof products==='undefined'||!Array.isArray(products))return;const items=products.filter(p=>p.featured).slice(0,8);g.innerHTML=items.map(product=>`<article class="group bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 overflow-hidden"><a href="product-detail.html?id=${product.id}" class="block"><div class="relative aspect-[3/4] overflow-hidden bg-gray-100"><img src="${product.image}" alt="${product.name}" class="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500" loading="lazy"><div class="absolute inset-0 bg-black/0 group-hover:bg-black/10 transition-colors duration-300"></div></div><div class="p-4"><h3 class="font-medium text-gray-900 mb-2 line-clamp-2 group-hover:text-blue-600 transition-colors">${product.name}</h3><p class="text-blue-600 font-semibold text-lg">${formatCurrency(product.price)}</p></div></a></article>`).join('')}
function initFeaturedProductsSection(){const c=document.querySelector('#featuredProductsGrid');if(!c)return;let t=0;const max=15;const timer=setInterval(()=>{t+=1;if(typeof products!=='undefined'&&Array.isArray(products)){clearInterval(timer);renderFeaturedProducts()}else if(t>=max){clearInterval(timer);c.innerHTML='<p class="col-span-full text-center text-gray-500 py-12">Không thể tải dữ liệu sản phẩm. Vui lòng tải lại trang.</p>'}},100)}
function renderAllProducts(productsToRender){const g=document.querySelector('.shop-products .product-grid');if(!g)return;const src=Array.isArray(productsToRender)?productsToRender:(typeof products!=='undefined'&&Array.isArray(products)?products:null);if(!src)return;if(src.length===0){g.innerHTML='<p class="col-span-full text-center text-gray-500 py-12">Không tìm thấy sản phẩm nào phù hợp.</p>';return}g.innerHTML=src.map(product=>`<article class="group bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 overflow-hidden"><a href="product-detail.html?id=${product.id}" class="block"><div class="relative aspect-[3/4] overflow-hidden bg-gray-100"><img src="${product.image}" alt="${product.name}" class="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500" loading="lazy"><div class="absolute inset-0 bg-black/0 group-hover:bg-black/10 transition-colors duration-300"></div></div><div class="p-4"><h3 class="font-medium text-gray-900 mb-2 line-clamp-2 group-hover:text-blue-600 transition-colors">${product.name}</h3><p class="text-blue-600 font-semibold text-lg">${formatCurrency(product.price)}</p></div></a></article>`).join('')}
function initShopProductsSection(){const grid=document.querySelector('.shop-products .product-grid');if(!grid)return;let t=0;const max=15;const timer=setInterval(()=>{t+=1;if(typeof products!=='undefined'&&Array.isArray(products)){clearInterval(timer);renderAllProducts(products)}else if(t>=max){clearInterval(timer);grid.innerHTML='<p class="col-span-full text-center text-gray-500 py-12">Không thể tải dữ liệu sản phẩm. Vui lòng tải lại trang.</p>'}},100)}
function setupShopFilters(){const searchInput=document.querySelector('#searchInput');const categoryFilters=document.querySelectorAll('.category-filter');const priceFilters=document.querySelectorAll('.price-filter');const clearBtn=document.querySelector('#clearFilters');if(!searchInput)return;let activeCategory=null;let activePriceRange=null;let searchTerm='';function filterProducts(){let filtered=[...products];if(activeCategory){filtered=filtered.filter(p=>p.category===activeCategory)}if(activePriceRange){const r=priceRanges.find(x=>x.id===activePriceRange);if(r){filtered=filtered.filter(p=>p.price>=r.min&&p.price<r.max)}}if(searchTerm){const s=searchTerm.toLowerCase();filtered=filtered.filter(p=>p.name.toLowerCase().includes(s)||p.description.toLowerCase().includes(s))}renderAllProducts(filtered);updateResultCount(filtered.length)}function updateResultCount(c){const el=document.querySelector('.result-count');if(el){el.textContent=`Hiển thị ${c} sản phẩm`}}searchInput.addEventListener('input',e=>{searchTerm=e.target.value.trim();filterProducts()});categoryFilters.forEach(f=>{f.addEventListener('change',e=>{if(e.target.checked){activeCategory=e.target.value;categoryFilters.forEach(x=>{if(x!==e.target)x.checked=false})}else{activeCategory=null}filterProducts()})});priceFilters.forEach(f=>{f.addEventListener('change',e=>{if(e.target.checked){activePriceRange=e.target.value;priceFilters.forEach(x=>{if(x!==e.target)x.checked=false})}else{activePriceRange=null}filterProducts()})});if(clearBtn){clearBtn.addEventListener('click',()=>{activeCategory=null;activePriceRange=null;searchTerm='';searchInput.value='';categoryFilters.forEach(f=>f.checked=false);priceFilters.forEach(f=>f.checked=false);filterProducts()})}filterProducts()}
function renderProductDetail(){const c=document.querySelector('.product-detail-container');if(!c)return;const params=new URLSearchParams(window.location.search);const id=parseInt(params.get('id'));const product=products.find(p=>p.id===id);if(!product){c.innerHTML='<p class="text-center text-red-500 py-12">Sản phẩm không tồn tại.</p>';return}c.innerHTML=`<div class="grid lg:grid-cols-2 gap-8 lg:gap-12"><div class="order-2 lg:order-1"><div class="rounded-2xl overflow-hidden shadow-2xl"><img src="${product.image}" alt="${product.name}" class="w-full h-auto object-cover" width="600" height="750"></div></div><div class="order-1 lg:order-2"><div class="space-y-6"><nav class="text-sm text-gray-500"><a href="index.html" class="hover:text-blue-600">Trang chủ</a><span class="mx-2">/</span><a href="shop.html" class="hover:text-blue-600">Cửa hàng</a><span class="mx-2">/</span><span class="text-gray-900">${product.name}</span></nav><h1 class="font-display text-3xl lg:text-4xl font-bold text-gray-900">${product.name}</h1><div class="flex items-baseline space-x-3"><p class="text-3xl font-bold text-blue-600">${formatCurrency(product.price)}</p></div><div class="prose prose-gray"><h3 class="font-semibold text-lg mb-2">Mô tả sản phẩm</h3><p class="text-gray-600 leading-relaxed">${product.description}</p></div><div class="bg-gray-50 rounded-xl p-6"><h3 class="font-semibold text-lg mb-4">Đặc điểm nổi bật</h3><ul class="space-y-2"><li class="flex items-center text-gray-700"><i class="fas fa-check-circle text-green-500 mr-3"></i>Chất liệu cao cấp, thoáng mát</li><li class="flex items-center text-gray-700"><i class="fas fa-check-circle text-green-500 mr-3"></i>Thiết kế tối giản, sang trọng</li><li class="flex items-center text-gray-700"><i class="fas fa-check-circle text-green-500 mr-3"></i>Đổi trả miễn phí trong 7 ngày</li><li class="flex items-center text-gray-700"><i class="fas fa-check-circle text-green-500 mr-3"></i>Giao hàng toàn quốc</li></ul></div><button class="add-to-cart-btn w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-4 px-8 rounded-lg transition-colors duration-300 transform hover:scale-105" aria-label="Thêm ${product.name} vào giỏ hàng"><i class="fas fa-shopping-cart mr-2"></i>Thêm Vào Giỏ Hàng</button><div class="border-t pt-6 space-y-3 text-sm text-gray-600"><p><i class="fas fa-truck mr-2 text-blue-600"></i> Miễn phí vận chuyển cho đơn hàng từ 500.000đ</p><p><i class="fas fa-shield-alt mr-2 text-blue-600"></i> Cam kết hàng chính hãng 100%</p><p><i class="fas fa-headset mr-2 text-blue-600"></i> Hỗ trợ khách hàng 24/7</p></div></div></div></div>`;document.title=`${product.name} - AuraStyle`;const btn=document.querySelector('.add-to-cart-btn');if(btn){btn.addEventListener('click',()=>{showNotification('Sản phẩm đã được thêm vào giỏ hàng!','success')})}}
function setupContactForm(){const f=document.querySelector('#contactForm');if(!f)return;f.addEventListener('submit',e=>{e.preventDefault();const name=document.querySelector('#name').value.trim();const email=document.querySelector('#email').value.trim();const message=document.querySelector('#message').value.trim();if(!name||!email||!message){alert('Vui lòng điền đầy đủ thông tin!');return}const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;if(!emailRegex.test(email)){alert('Email không hợp lệ!');return}alert('Cảm ơn bạn đã liên hệ! Chúng tôi sẽ phản hồi sớm nhất có thể.');f.reset()})}
document.querySelectorAll('a[href^="#"]').forEach(a=>{a.addEventListener('click',function(e){const href=this.getAttribute('href');if(href==='#')return;e.preventDefault();const target=document.querySelector(href);if(target){target.scrollIntoView({behavior:'smooth',block:'start'})}})});
function setupMobileFilterToggle(){const t=document.querySelector('.filter-toggle');const s=document.querySelector('.shop-sidebar');const c=document.querySelector('.filter-close');if(t&&s){t.addEventListener('click',()=>{s.classList.add('active');document.body.classList.add('no-scroll')})}if(c&&s){c.addEventListener('click',()=>{s.classList.remove('active');document.body.classList.remove('no-scroll')})}}
function setupNewsletterForm(){const f=document.querySelector('#newsletterForm');if(!f)return;f.addEventListener('submit',e=>{e.preventDefault();const email=f.querySelector('input[type="email"]');if(email.value.trim()){showNotification('Cảm ơn bạn đã đăng ký nhận tin! Chúng tôi sẽ gửi thông tin mới nhất đến email của bạn.','success');f.reset()}})}
document.addEventListener('DOMContentLoaded',()=>{initFeaturedProductsSection();initShopProductsSection();setupShopFilters();renderProductDetail();setupContactForm();setupMobileFilterToggle();setupNewsletterForm()});
if(document.readyState!=='loading'){try{initFeaturedProductsSection()}catch(e){console.warn(e)}}
